import Stack from '@mui/material/Stack';
import {useRef} from 'react';

function Main(){
    const inputRef = useRef(null);

    const calculateAll = () => {
        console.log(inputRef.current.value);
    };
    return(<>
    <form>
        <Stack direction="row" spacing={2}>
            <label>
            Year:
            <input type="text" name="year" ref={inputRef}/>
            </label>
            <label>
            Month:
            <input type="text" name="month" />
            </label>
            <label>
            Day:
            <input type="text" name="day" />
            </label>
            <input type="submit" value="Submit" onClick={calculateAll} />
        </Stack>

    </form>
    </>)
}

export default Main;
