import Stack from '@mui/material/Stack';
import {useState} from 'react';

function Main(){
    const [birthDay, setDay] = useState(0);
    const [birthMonth, setMonth] = useState(0);
    const [birthYear, setYear] = useState(0);
    const daysInMonth = [31,28,31,30,31,30,31,31,30,31,30,31];

    const calculateAll = () => {
        const current = new Date();
        const date = `${current.getDate()}/${current.getMonth()+1}/${current.getFullYear()}`;
        console.log(date);

        console.log(birthDay);
        console.log(birthMonth);
        console.log(birthYear);

        let daysAlive = current.getDate()-birthDay;
        let monthsAlive = current.getMonth()+1-birthMonth;
        let yearsAlive = current.getFullYear()-birthYear;
        if (daysAlive < 0){
            if (monthsAlive < 12){
                daysAlive = daysAlive + daysInMonth[monthsAlive];
                monthsAlive = monthsAlive + 1;
            }else{
                daysAlive = daysAlive + daysInMonth[monthsAlive];
                monthsAlive = 1;
            }
        }
        console.log(daysAlive);
        console.log(monthsAlive);
        console.log(yearsAlive);

    };

    return(<>
    <form>
        <Stack direction="column" spacing={2}>
            <label>
            Year:
            <input 
                type="text" 
                name="year" 
                onChange={(e) => setYear(parseInt(e.target.value))}
            />
            </label>
            <label>
            Month:
            <input 
                type="text" 
                name="month" 
                onChange={(e) => setMonth(parseInt(e.target.value))}
            />
            </label>
            <label>
            Day:
            <input 
                type="text" 
                name="day" 
                onChange={(e) => setDay(parseInt(e.target.value))}
                />
            </label>
        </Stack>

    </form>
            <button onClick={calculateAll} >Calculate</button>

    </>)
}

export default Main;
